mapper.select:
SELECT sf.*,d.`dd_valueId` `sf_Type.dd_ValueId`,d.`dd_valueName` `sf_Type.dd_ValueName`,e.`e_id` `sf_Create.e_Id`,e.`e_realName` `sf_Create.e_RealName` FROM service_FAQ sf INNER JOIN data_dictionary d ON sf.`sf_type`=d.`dd_valueId` INNER JOIN employees e ON sf.`sf_create`=e.`e_id` WHERE d.`dd_typeCode`='service_type' AND sf.`sf_title` LIKE CONCAT('%',#{faq.sf_Title},'%')
AND sf.`sf_type`=#{faq.sf_Type.dd_ValueId}
ORDER BY sf.sf_id LIMIT #{start},#{length}

mapper.update:
update service_faq
set sf_lastTime=default ,sf_type=#{faq.sf_Type.dd_ValueId},sf_point=#{faq.sf_Point},sf_description=#{faq.sf_Description}
where sf_id=#{faq.sf_Id}
