mapper.select:
SELECT s.*,type.`dd_valueId` `s_Type.dd_ValueId`,type.`dd_valueName` `s_Type.dd_ValueName`,status.`dd_valueId` `s_status.dd_ValueId`,status.`dd_valueName` `s_status.dd_ValueName`,op.`e_realName` `s_operator.e_RealName`,pro.`e_realName` `s_processor.e_RealName` FROM servicees s INNER JOIN data_dictionary TYPE ON type.`dd_valueId`=s.`s_type` INNER JOIN data_dictionary STATUS ON status.`dd_valueId`=s.`s_status` INNER JOIN employees op ON op.`e_id`=s.`s_operator` INNER JOIN employees pro ON pro.`e_id`=s.`s_processor` WHERE type.`dd_typeCode`='service_type' AND status.`dd_typeCode`='service_status' AND (s.`s_operator` = #{ser.s_Operator.e_Id} OR s.`s_processor` = #{ser.s_Processor.e_Id}) AND s.`s_name` LIKE CONCAT('%',#{ser.s_Name},'%')
AND s.`s_type` = #{ser.s_Type.dd_ValueId}
AND s.`s_status` = #{ser.s_Status.dd_ValueId}
ORDER BY s.`s_id` LIMIT #{start},#{length}

SELECT COUNT(*) FROM servicees s INNER JOIN employees op ON op.`e_id`=s.`s_operator` INNER JOIN employees pro ON pro.`e_id`=s.`s_processor`
AND (s.`s_operator` = #{ser.s_Operator.e_Id} OR s.`s_processor` = #{ser.s_Processor.e_Id}) AND s.`s_name` LIKE CONCAT('%',#{ser.s_Name},'%')
AND s.`s_type` = #{ser.s_Type.dd_ValueId}
AND s.`s_status` = #{ser.s_Status.dd_ValueId}

mapper.update:
UPDATE servicees s
SET s.`s_info`=#{ser.s_info},s.`s_description`=#{ser.s_description},s.`s_iPhone`=#{ser.s_iPhone},s.`s_type` = #{ser.s_Type.dd_ValueId} , s_lastTime = DEFAULT
WHERE s_id = #{ser.s_Id}
